<script>
    $(document).ready(function() {
        $('#auto-download').on('click', function() {
            var status = $(this).is(':checked') ? 1 : 0;

            // Send Ajax request to update status
            $.ajax({
                type: 'POST',
                url: '{{ route("updateAutomaticallyUpload") }}',
                data: {
                    '_token': '{{ csrf_token() }}',
                    'status': status
                },
                success: function(data) {
                    toastr.success(data.success);
                    return true; // Handle success response
                },
                error: function(error) {
                    toastr.error(data.error); // Handle error response
                }
            });
        });
    });
</script>
<script>
    document.getElementById('redirectAndPopupBtn').addEventListener('click', function() {
        // Redirect to a new page
        // Open a popup window
        window.location.href = '{{ route('passwords.index') }}';
        localStorage.setItem('popupOpened', true);
    });

    document.getElementById('support-ticket').addEventListener('click', function() {
        // Redirect to a new page
        // Open a popup window
        window.location.href = '{{ route('support-tickets.index') }}';
        localStorage.setItem('createTicket', true);
    });

</script>
